        citiesArr3.add("Pandora");

        List<String> citiesArr4 = new ArrayList<>();

        Integer a = 82;
        Integer b = 115;
        Integer c = 0;
        Integer d = 0;

        assertEquals(a, app.businessTrip(citiesGraph, citiesArr));
